        public void DesirilisationCategory()
        {
            BinaryFormatter formatter = new BinaryFormatter();

            using (FileStream fs = new FileStream("CategoryPhoto.dat", FileMode.OpenOrCreate))
            {
                ShopListFF = (ObservableCollection<CategoryModel>)formatter.Deserialize(fs);
                //foreach (var gg in ShopList)
                //{
                //    if ((gg.NameCategory!= "МОДА&СТИЛЬ")|| (gg.NameCategory != "КРАСОТА&ЗДОРОВЬЕ") || (gg.NameCategory != "ДЕТИ"))
                //    {
                //        ShopList.Remove(gg); 
                //    }
                //}
                foreach (var g in ShopList)
                {
                    foreach (var r in ShopListFF)
                    {
                        if (r.NameCategory.Contains(g.NameCategory))
                        {
                            g.PathCategoryPhoto = r.PathCategoryPhoto;
                        }
                    }
                }
                ShopList[1].PathCategoryPhoto = ShopListFF[1].PathCategoryPhoto;
            }
        }
